\",\"depth\":0,\"inlineStyleRanges\":[{\"offset\":0,\"length\":79,\"style\":\"color-rgb(0,0,0)\"},{\"offset\":0,\"length\":79,\"style\":\"bgcolor-rgb(255,255,255)\"},{\"offset\":0,\"length\":79,\"style\":\"fontsize-15\"},{\"offset\":0,\"length\":79,\"style\":\"fontfamily-Verdana, sans-serif\"}],\"entityRanges\":[],\"data\":{\"text-align\":\"justify\"}},{\"key\":\"dq7g8\",\"text\":\"OOP is faster and easier to execute\",\"type\":\"unordered-list-item\",\"depth\":0,\"inlineStyleRanges\":[{\"offset\":0,\"length\":35,\"style\":\"color-rgb(0,0,0)\"},{\"offset\":0,\"length\":35,\"style\":\"bgcolor-rgb(255,255,255)\"},{\"offset\":0,\"length\":35,\"style\":\"fontsize-15\"},{\"offset\":0,\"length\":35,\"style\":\"fontfamily-Verdana, sans-serif\"}],\"entityRanges\":[],\"data\":{\"text-align\":\"justify\"}},{\"key\":\"53t0p\",\"text\":\"OOP provides a clear structure for the programs\",\"type\":\"unordered-list-item\",\"depth\":0,\"inlineStyleRanges\":[{\"offset\":0,\"length\":47,\"style\":\"color-rgb(0,0,0)\"},{\"offset\":0,\"length\":47,\"style\":\"bgcolor-rgb(255,255,255)\"},{\"offset\":0,\"length\":47,\"style\":\"fontsize-15\"},{\"offset\":0,\"length\":47,\"style\":\"fontfamily-Verdana, sans-serif\"}],\"entityRanges\":[],\"data\":{\"text-align\":\"justify\"}},{\"key\":\"62qvp\",\"text\":\"OOP helps to keep the C++ code DRY \\\"Don\'t Repeat Yourself\\\", and makes the code easier to maintain, modify and debug\",\"type\":\"unordered-list-item\",\"depth\":0,\"inlineStyleRanges\":[{\"offset\":0,\"length\":115,\"style\":\"color-rgb(0,0,0)\"},{\"offset\":0,\"length\":115,\"style\":\"bgcolor-rgb(255,255,255)\"},{\"offset\":0,\"length\":115,\"style\":\"fontsize-15\"},{\"offset\":0,\"length\":115,\"style\":\"fontfamily-Verdana, sans-serif\"}],\"entityRanges\":[],\"data\":{\"text-align\":\"justify\"}},{\"key\":\"4ohpq\",\"text\":\"OOP makes it possible to create full reusable applications with less code and shorter development time\",\"type\":\"unordered-list-item\",\"depth\":0,\"i